import { COMPONENTS_DATA } from './data/components';

interface Contributor {
  id: number;
  login: string;
  avatar_url: string;
  html_url: string;
}

interface ContributorsSectionProps {
  contributors: Contributor[];
}

interface SocialProofSectionProps {
  stargazers: string;
  npmDownloads: string;
  discordMembers: string;
}

export default function HomePageContent() {
  const mainRef = useRef<HTMLDivElement>(null);
  const [contributors, setContributors] = useState([]);
  const [stargazers, setStargazers] = useState('');
  const [npmDownloads, setNpmDownloads] = useState('');
  const [discordMembers, setDiscordMembers] = useState('');

  useEffect(() => {
    fetch('https://api.github.com/repos/themesberg/flowbite-react/contributors?per_page=21')
      .then((res) => res.json())
      .then((data) => {
        setContributors(data);
      });

    fetch('https://api.github.com/repos/themesberg/flowbite-react')
      .then((res) => res.json())
      .then((data) => setStargazers(numberWithCommas(data.stargazers_count)));

    fetch('https://api.npmjs.org/downloads/point/2021-01-01:2100-01-01/flowbite-react')
      .then((res) => res.json())
      .then((data) => setNpmDownloads(numberWithCommas(data.downloads)));

    fetch('https://discord.com/api/v9/invites/4eeurUVvTy?with_counts=true&with_expiration=true')
      .then((res) => res.json())
      .then((data) => setDiscordMembers(numberWithCommas(data.approximate_presence_count)));
  }, []);

  const numberWithCommas = (x: string | number) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  };

  return (
    <Flowbite>
      <div className="relative max-h-screen w-full overflow-auto bg-white text-gray-600 antialiased dark:bg-gray-900 dark:text-gray-400">
        <Banner />
        <HomeNavbar />
        <div className="lg:flex">
          <main
            className="min-w-0 flex-auto divide-y dark:divide-gray-700 lg:static lg:max-h-full lg:overflow-visible"
            ref={mainRef}
          >
            <HeroSection />
            <FeaturedSection />
            <ComponentsSection />
            <ReactSection />
            <DarkModeSection />
            <TailwindSection />
            <SocialProofSection stargazers={stargazers} npmDownloads={npmDownloads} discordMembers={discordMembers} />
            <FigmaSection />
            <ContributorsSection contributors={contributors} />
          </main>
        </div>
        <MainFooter />
      </div>
    </Flowbite>
  );
}

